function [A, b] = generareSistem(n)% genereaza sisteme de dimensiune n% A - matrice de dimensiune n x n, matricea coeficientilor% b - matrice coloana de dimensiune n, matricea termenilor liberimaxVal = 25;A = unifrnd(-maxVal, maxVal, n);while det(A) == 0  A = unifrnd(-maxVal, maxVal, n);end% pentru ca solutia sa fie [1,...,1]T trebuie ca suma b(i) sa fie egal cu % suma elementelor pe linie in matricea Ab = sum(A,2);